import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FiLogOut, FiPlusCircle, FiSearch, FiUser, FiLayers, FiHome, FiCompass } from "react-icons/fi";
import AddCourseForm from "../components/AddCourseForm";
import CourseCard from "../components/CourseCard";

const Explore = () => {
  const [skills, setSkills] = useState([]);
  const [search, setSearch] = useState("");
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    } else {
      fetchUserProfile(token);
      fetchSkills();
    }
  }, []);

  const fetchUserProfile = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/auth/profile", {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      if (response.ok) {
        setUser(data);
      } else {
        alert("Session expired! Please log in again.");
        localStorage.removeItem("token");
        navigate("/login");
      }
    } catch (error) {
      console.error("Error fetching user profile:", error);
    }
  };

  const fetchSkills = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/skills");
      const data = await response.json();
      setSkills(data);
    } catch (error) {
      console.error("Error fetching skills:", error);
    }
  };

  const handleAddCourse = async (courseData) => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        alert("You are not logged in. Please log in to add a course.");
        return;
      }

      const response = await fetch("http://localhost:5000/api/skills", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(courseData),
      });

      if (response.ok) {
        alert("Course added successfully!");
        fetchSkills(); // Refresh the list of skills
      } else {
        const errorData = await response.json();
        console.error("Error response from server:", errorData);
        alert(errorData.message || "Failed to add course. Please try again.");
      }
    } catch (error) {
      console.error("Error adding course:", error);
      alert("An error occurred while adding the course. Please try again.");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-lg p-5 flex flex-col">
        <h2 className="text-2xl font-bold text-purple-700 mb-6 flex items-center">
          <FiLayers className="mr-2" /> Virtual Time Bank
        </h2>

        <nav className="flex flex-col space-y-4">
          <button onClick={() => navigate("/")} className="flex items-center p-3 rounded-lg hover:bg-purple-100">
            <FiHome className="mr-3" /> Home
          </button>

          <button onClick={() => navigate("/profile")} className="flex items-center p-3 rounded-lg hover:bg-purple-100">
            <FiUser className="mr-3" /> Profile
          </button>

          <button onClick={() => navigate("/explore")} className="flex items-center p-3 rounded-lg hover:bg-blue-100">
            <FiCompass className="mr-3" /> Explore
          </button>

          <button
            onClick={() => navigate("/transactions")}
            className="flex items-center p-3 rounded-lg hover:bg-purple-100"
          >
            ðŸ“œ Transaction History
          </button>

          <button onClick={handleLogout} className="flex items-center p-3 rounded-lg text-red-500 hover:bg-red-100">
            <FiLogOut className="mr-3" /> Logout
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <div className="bg-white shadow p-4 flex justify-between items-center">
          <h2 className="text-2xl font-semibold text-purple-700">Explore</h2>
          <div className="flex items-center space-x-4">
            <input
              type="text"
              placeholder="Search courses..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="border p-2 rounded-md focus:ring-2 focus:ring-purple-400"
            />
            {user && (
              <div className="bg-gray-200 px-4 py-2 rounded-lg text-gray-700">
                {user.name} ({user.timeCredits} Credits)
              </div>
            )}
          </div>
        </div>

        {/* Content */}
        <div className="p-6">
          <h2 className="text-2xl font-bold text-purple-700 mb-4">Explore Courses</h2>

          {/* Add New Course Form */}
          <div className="bg-white shadow-md rounded-lg p-6 mb-6">
            <AddCourseForm onAddCourse={handleAddCourse} />
          </div>

          {/* Display Courses */}
          {skills.length > 0 ? (
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {skills
                .filter((skill) => skill.name.toLowerCase().includes(search.toLowerCase()))
                .map((skill) => (
                  <div
                    key={skill._id}
                    onClick={() => navigate(`/course/${skill._id}`)}
                  >
                    <CourseCard course={skill} />
                  </div>
                ))}
            </div>
          ) : (
            <p className="text-gray-500">No courses available yet.</p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Explore;
