import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import AddSkill from "./AddSkill";
import { FiLogOut, FiPlusCircle, FiSearch, FiUser, FiLayers, FiHome, FiCompass } from "react-icons/fi";
import axios from "axios";

function Dashboard() {
  const [user, setUser] = useState(null);
  const [skills, setSkills] = useState([]);
  const [search, setSearch] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (!token) {
      navigate("/login");
    } else {
      fetchUserProfile(token);
      fetchSkills();
    }
  }, []);

  const fetchUserProfile = async (token) => {
    try {
      const response = await fetch("http://localhost:5000/api/auth/profile", {
        method: "GET",
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      if (response.ok) {
        setUser(data);
      } else {
        alert("Session expired! Please log in again.");
        localStorage.removeItem("token");
        navigate("/login");
      }
    } catch (error) {
      console.error("Error fetching user profile:", error);
    }
  };

  const fetchSkills = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/skills");
      const data = await response.json();
      if (response.ok) {
        setSkills(data);
      } else {
        alert("Failed to load skills.");
      }
    } catch (error) {
      console.error("Error fetching skills:", error);
    }
  };

  // âœ… Handle Earning Time Credits
  const handleEarnCredits = async (skillId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `http://localhost:5000/api/transactions/earn`,
        { skillId },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(response.data.message);
      fetchUserProfile(token);
    } catch (error) {
      console.error("Error earning credits:", error);
      alert(error.response?.data?.message || "Error earning credits");
    }
  };

  // âœ… Handle Spending Time Credits
  const handleSpendCredits = async (skillId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.post(
        `http://localhost:5000/api/transactions/spend`,
        { skillId },
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      alert(response.data.message);
      fetchUserProfile(token);
    } catch (error) {
      console.error("Error spending credits:", error);
      alert(error.response?.data?.message || "Error spending credits");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-lg p-5 flex flex-col">
        <h2 className="text-2xl font-bold text-purple-700 mb-6 flex items-center">
          <FiLayers className="mr-2" /> Virtual Time Bank
        </h2>

        <nav className="flex flex-col space-y-4">
          <button onClick={() => navigate("/")} className="flex items-center p-3 rounded-lg hover:bg-purple-100">
            <FiHome className="mr-3" /> Home
          </button>

          <button onClick={() => navigate("/profile")} className="flex items-center p-3 rounded-lg hover:bg-purple-100">
            <FiUser className="mr-3" /> Profile
          </button>

          <button onClick={() => navigate("/")} className="flex items-center p-3 rounded-lg hover:bg-blue-100">
            <FiCompass className="mr-3" /> Explore
          </button>
          <button onClick={handleLogout} className="flex items-center p-3 rounded-lg text-red-500 hover:bg-red-100">
            <FiLogOut className="mr-3" /> Logout
          </button>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <div className="bg-white shadow p-4 flex justify-between items-center">
          <h2 className="text-2xl font-semibold text-purple-700">Dashboard</h2>
          <div className="flex items-center space-x-4">
            <input
              type="text"
              placeholder="Search skills..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="border p-2 rounded-md focus:ring-2 focus:ring-purple-400"
            />
            {user && (
              <div className="bg-gray-200 px-4 py-2 rounded-lg text-gray-700">
                {user.name} ({user.timeCredits} Credits)
              </div>
            )}
          </div>
        </div>

        {/* User Info & Skills */}
        <div className="p-6">
          {user ? (
            <>
              <div className="bg-white shadow-lg p-6 rounded-lg mb-6">
                <h3 className="text-xl font-semibold text-purple-600">
                  Welcome, {user.name}!
                </h3>
                <p className="text-gray-600">Email: {user.email}</p>
                <p className="text-gray-700">
                  Time Credits: <strong>{user.timeCredits}</strong>
                </p>
              </div>

              <AddSkill />

              <h3 className="text-xl font-bold text-purple-700 mt-6">Available Skills</h3>

              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mt-4">
                {skills.length > 0 ? (
                  skills
                    .filter((skill) => skill.name.toLowerCase().includes(search.toLowerCase()))
                    .map((skill) => (
                      <div key={skill._id} className="bg-white p-6 shadow-lg rounded-lg hover:shadow-xl transition">
                        <h4 className="text-lg font-semibold text-gray-800 flex items-center">
                          <span className="text-purple-600 mr-2">ðŸ”¹</span> {skill.name}
                        </h4>
                        <p className="text-gray-600 mt-2">{skill.description}</p>
                        <div className="flex justify-between items-center mt-4">
                          <p className="text-sm font-semibold text-purple-700">
                            ðŸ•’ {skill.hoursAvailable} hours available
                          </p>
                          <p className="text-sm text-gray-500">
                            ðŸ‘¤ {skill.user?.name || "Unknown"}
                          </p>
                        </div>
                        <div className="flex justify-between mt-4">
                          <button
                            onClick={() => handleEarnCredits(skill._id)}
                            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
                          >
                            Earn Credits
                          </button>
                          <button
                            onClick={() => handleSpendCredits(skill._id)}
                            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
                          >
                            Spend Credits
                          </button>
                        </div>
                      </div>
                    ))
                ) : (
                  <p className="text-center text-gray-500 col-span-3">
                    No skills available.
                  </p>
                )}
              </div>
            </>
          ) : (
            <p className="text-center text-gray-500">Loading...</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
