import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { FiUsers, FiPackage, FiDollarSign, FiCreditCard } from "react-icons/fi";
import AdminSidebar from "../../components/admin/AdminSidebar";
import StatsCard from "../../components/admin/StatsCard";
import { getAdminStats } from "../../services/adminService";

const AdminDashboard = () => {
  const [stats, setStats] = useState(null);
  const [recentTransactions, setRecentTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const checkAdmin = async () => {
      const token = localStorage.getItem("adminToken");
      if (!token) {
        navigate("/admin/login");
        return;
      }
      
      try {
        const data = await getAdminStats();
        setStats(data.stats);
        setRecentTransactions(data.recentTransactions);
      } catch (error) {
        console.error("Error fetching admin stats:", error);
        if (error.response?.status === 403) {
          navigate("/admin/login");
        }
      } finally {
        setLoading(false);
      }
    };
    
    checkAdmin();
  }, [navigate]);

  if (loading) {
    return (
      <div className="flex h-screen items-center justify-center">
        <div className="animate-spin rounded-full h-16 w-16 border-t-2 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  return (
    <div className="flex h-screen bg-gray-100">
      <AdminSidebar />
      
      <div className="flex-1 overflow-auto">
        <div className="p-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-6">Dashboard Overview</h1>
          
          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <StatsCard
              title="Total Users"
              value={stats?.totalUsers || 0}
              icon={<FiUsers size={24} />}
              color="bg-blue-500"
            />
            <StatsCard
              title="Total Skills"
              value={stats?.totalSkills || 0}
              icon={<FiPackage size={24} />}
              color="bg-green-500"
            />
            <StatsCard
              title="Total Transactions"
              value={stats?.totalTransactions || 0}
              icon={<FiDollarSign size={24} />}
              color="bg-yellow-500"
            />
            <StatsCard
              title="Total Time Credits"
              value={stats?.totalCredits || 0}
              icon={<FiCreditCard size={24} />}
              color="bg-purple-500"
            />
          </div>
          
          {/* Recent Transactions */}
          <div className="bg-white rounded-lg shadow-md p-6 mb-8">
            <h2 className="text-xl font-semibold mb-4">Recent Transactions</h2>
            <div className="overflow-x-auto">
              <table className="min-w-full bg-white">
                <thead className="bg-gray-100">
                  <tr>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">User</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Skill</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Type</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Amount</th>
                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {recentTransactions.map((transaction) => (
                    <tr key={transaction._id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {transaction.user?.name || 'Unknown User'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {transaction.skill?.name || 'Unknown Skill'}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full
                          ${transaction.type === 'earn' ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}`}
                        >
                          {transaction.type}
                        </span>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        {transaction.amount}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {new Date(transaction.createdAt).toLocaleString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
              {recentTransactions.length === 0 && (
                <p className="text-center py-4 text-gray-500">No recent transactions found</p>
              )}
            </div>
            
            <div className="mt-4 text-right">
              <button 
                onClick={() => navigate("/admin/transactions")}
                className="text-indigo-600 hover:text-indigo-800"
              >
                View All Transactions â†’
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
