import React, { useEffect, useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import {
  FiLogIn,
  FiUserPlus,
  FiHome,
  FiLayers,
  FiLogOut,
  FiCompass,
} from "react-icons/fi";

function Home() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [skills, setSkills] = useState([]);
  const navigate = useNavigate();
  const skillsRef = useRef(null);

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      setIsLoggedIn(true);
    }
    fetchSkills();
  }, []);

  const fetchSkills = async () => {
    try {
      const response = await fetch("http://localhost:5000/api/skills");
      const data = await response.json();
      setSkills(data);
    } catch (error) {
      console.error("Error fetching skills:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const scrollToSkills = () => {
    if (skillsRef.current) {
      skillsRef.current.scrollIntoView({ behavior: "smooth" });
    }
  };

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar */}
      <div className="w-64 bg-white shadow-md p-5 flex flex-col">
        <h2 className="text-2xl font-bold text-purple-600 mb-6 flex items-center">
          <FiLayers className="mr-2" /> Virtual Time Bank
        </h2>
        <nav className="flex flex-col space-y-4">
          <button
            onClick={() => navigate("/")}
            className="flex items-center p-3 rounded-lg hover:bg-gray-100"
          >
            <FiHome className="mr-3" /> Home
          </button>
          {!isLoggedIn ? (
            <>
              <button
                onClick={() => navigate("/login")}
                className="flex items-center p-3 rounded-lg hover:bg-gray-100"
              >
                <FiLogIn className="mr-3" /> Login
              </button>
              <button
                onClick={() => navigate("/signup")}
                className="flex items-center p-3 rounded-lg hover:bg-gray-100"
              >
                <FiUserPlus className="mr-3" /> Sign Up
              </button>
            </>
          ) : (
            <>
              <button
                onClick={() => navigate("/dashboard")}
                className="flex items-center p-3 rounded-lg hover:bg-gray-100"
              >
                <FiLayers className="mr-3" /> Dashboard
              </button>
              <button
                onClick={() => navigate("/explore")}
                className="flex items-center p-3 rounded-lg hover:bg-gray-100"
              >
                <FiCompass className="mr-3" /> Explore
              </button>
              <button
                onClick={() => navigate("/transactions")}
                className="flex items-center p-3 rounded-lg hover:bg-purple-100"
              >
                ðŸ“œ Transaction History
              </button>
              <button
                onClick={handleLogout}
                className="flex items-center p-3 text-red-600 rounded-lg hover:bg-red-100"
              >
                <FiLogOut className="mr-3" /> Logout
              </button>
            </>
          )}
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Navbar */}
        <div className="bg-white shadow p-4 flex justify-between items-center">
          <h2 className="text-2xl font-semibold text-purple-600">Welcome</h2>
          <div className="flex space-x-4">
            {!isLoggedIn ? (
              <>
                <button
                  onClick={() => navigate("/login")}
                  className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
                >
                  Login
                </button>
                <button
                  onClick={() => navigate("/signup")}
                  className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition"
                >
                  Sign Up
                </button>
              </>
            ) : (
              <button
                onClick={() => navigate("/dashboard")}
                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
              >
                Go to Dashboard
              </button>
            )}
          </div>
        </div>

        {/* Hero Section */}
        <div className="flex flex-col items-center justify-center h-[calc(100vh-64px)] text-center px-6">
          <h2 className="text-4xl font-bold mb-4">Welcome to Virtual Time Bank</h2>
          <p className="text-lg mb-6 max-w-lg text-gray-600">
            Exchange your skills and earn time credits! Join our community and
            start saving time today.
          </p>
          <button
            onClick={scrollToSkills}
            className="px-6 py-3 bg-blue-600 text-white font-semibold rounded-full shadow-md hover:bg-blue-700 transition"
          >
            Get Started
          </button>
        </div>

        {/* Available Skills Section */}
        <div ref={skillsRef} className="p-6">
          <h3 className="text-3xl font-semibold text-purple-600 mb-6 text-center">
            Available Skills
          </h3>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
            {skills.length > 0 ? (
              skills.map((skill) => (
                <div
                  key={skill._id}
                  className="bg-white shadow-lg rounded-xl overflow-hidden transform hover:scale-105 transition-all duration-300 cursor-pointer"
                  onClick={() => navigate(`/skill/${skill._id}`)}
                >
                  {/* Skill Card Image */}
                  <div className="h-48 bg-gradient-to-r from-purple-500 to-indigo-600 flex items-center justify-center text-white text-3xl font-bold">
                    {skill.name?.charAt(0) || "?"}
                  </div>

                  {/* Skill Details */}
                  <div className="p-5 space-y-3">
                    <h4 className="text-xl font-semibold text-gray-800">
                      {skill.name}
                    </h4>
                    <p className="text-gray-600 text-sm">
                      <span className="font-bold text-indigo-500">
                        {skill.timeCreditValue} Credits
                      </span>
                    </p>
                    <p className="text-sm text-gray-500">
                      Created by:{" "}
                      <span className="font-medium text-gray-800">
                        {skill.user?.name || "Unknown"}
                      </span>
                    </p>

                    {/* Enroll Button */}
                    <button className="mt-4 px-4 py-2 w-full bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 transition">
                      Learn More
                    </button>
                  </div>
                </div>
              ))
            ) : (
              <p className="text-gray-500 text-center w-full">
                No skills available yet.
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
